mapper.select:
select 

from portfolio_metrics
where id_portfolio = #{idPortfolio,jdbcType=VARCHAR}
  and dt_market = #{dtMarket,jdbcType=DATE}
  and cd_metric = #{cdMetric,jdbcType=VARCHAR}

mapper.delete:
delete from portfolio_metrics
where id_portfolio = #{idPortfolio,jdbcType=VARCHAR}
  and dt_market = #{dtMarket,jdbcType=DATE}
  and cd_metric = #{cdMetric,jdbcType=VARCHAR}

mapper.insert:
insert into portfolio_metrics (id_portfolio, dt_market, cd_metric, 
  amt_metric, cd_currency, cd_source
  )
values (#{idPortfolio,jdbcType=VARCHAR}, #{dtMarket,jdbcType=DATE}, #{cdMetric,jdbcType=VARCHAR}, 
  #{amtMetric,jdbcType=DECIMAL}, #{cdCurrency,jdbcType=VARCHAR}, #{cdSource,jdbcType=VARCHAR}
  )

mapper.update:
update portfolio_metrics


    amt_metric = #{amtMetric,jdbcType=DECIMAL},


    cd_currency = #{cdCurrency,jdbcType=VARCHAR},


    cd_source = #{cdSource,jdbcType=VARCHAR},


where id_portfolio = #{idPortfolio,jdbcType=VARCHAR}
  and dt_market = #{dtMarket,jdbcType=DATE}
  and cd_metric = #{cdMetric,jdbcType=VARCHAR}

update portfolio_metrics
set amt_metric = #{amtMetric,jdbcType=DECIMAL},
  cd_currency = #{cdCurrency,jdbcType=VARCHAR},
  cd_source = #{cdSource,jdbcType=VARCHAR}
where id_portfolio = #{idPortfolio,jdbcType=VARCHAR}
  and dt_market = #{dtMarket,jdbcType=DATE}
  and cd_metric = #{cdMetric,jdbcType=VARCHAR}
